# create a 100x100 Float32 random array and an uninitialized arrayA = CUDA.rand(2^9, 2^9);B = CuArray{Float32, 2}(undef, 2^9, 2^9);# regular matrix multiplication uses cuBLAS under the hoodA * A# use LinearAlgebra for matrix multiplicationusing LinearAlgebramul!(B, A, A)# use cuSOLVER for QR factorizationqr(A)# solve equation A*X == BA \ B# use cuFFT for FFTusing CUDA.CUFFTfft(A)