using AMDGPUfunction vadd!(C, A, B)    i = workitemIdx().x + (workgroupIdx().x - 1) * workgroupDim().x    if i <= length(A)        @inbounds C[i] = A[i] + B[i]    end    returnendA, B = ROCArray(ones(2^9)*2), ROCArray(ones(2^9)*3);C = similar(A);nthreads = 256# smallest integer larger than or equal to length(A)/threadsnumblocks = cld(length(A), nthreads)# run using 256 threads@roc threads=nthreads blocks=numblocks vadd!(C, A, B)@assert all(Array(C) .== 5.0)