@jit(nopython=True)def _generate(data, nx, ny):    # Radius of the source disc    radius = nx / 6.0    for i in range(nx+2):        for j in range(ny+2):            # Distance of point i, j from the origin            dx = i - nx / 2 + 1            dy = j - ny / 2 + 1            if (dx * dx + dy * dy < radius * radius):                data[i,j] = T_DISC            else:                data[i,j] = T_AREA    # Boundary conditions    for i in range(nx+2):        data[i,0] = T_LEFT        data[i, ny+1] = T_RIGHT    for j in range(ny+2):        data[0,j] = T_UPPER        data[nx+1, j] = T_LOWER