## NVIDIA#using BenchmarkToolsusing CUDAA = rand(2^9, 2^9);A_d = CuArray(A);@btime $A * $A;@btime CUDA.@sync $A_d * $A_d;## AMD#using BenchmarkToolsusing AMDGPUA = rand(2^9, 2^9);A_d = ROCArray(A);@btime $A * $A;@btime begin   $A_d * $A_d;   AMDGPU.synchronize()end## Intel#using BenchmarkToolsusing oneAPIA = rand(2^9, 2^9);A_d = oneArray(A);@btime $A * $A;@btime $A_d * $A_d;## Apple#using BenchmarkToolsusing MetalA = rand(2^9, 2^9);A_d = MtlArray(A);@btime $A * $A;@btime $A_d * $A_d;