using Metalfunction vadd!(C, A, B)    i = thread_position_in_grid_1d()    if i <= length(A)        @inbounds C[i] = A[i] + B[i]    end    returnendA, B = MtlArray(ones(Float32, 2^9)*2), MtlArray(Float32, ones(2^9)*3);C = similar(A);nthreads = 256# smallest integer larger than or equal to length(A)/threadsnumblocks = cld(length(A), nthreads)# run using 256 threads@metal threads=nthreads grid=numblocks vadd!(C, A, B)@assert all(Array(C) .== 5.0)