using oneAPI# WARNING: this is still untested on Intel GPUsfunction vadd!(C, A, B)    i = get_global_id()    if i <= length(a)        c[i] = a[i] + b[i]    end    returnendA, B = oneArray(ones(2^9)*2), oneArray(ones(2^9)*3);C = similar(A);nthreads = 256# smallest integer larger than or equal to length(A)/threadsnumgroups = cld(length(a),256)@oneapi items=nthreads groups=numgroups vadd!(c, a, b)@assert all(Array(C) .== 5.0)