//// OpenAcc//#include <stdio.h>#include <openacc.h>#define NX 102400int main(void){    double vecA[NX], vecB[NX], vecC[NX];    // Initialization of the vectors    for (int i = 0; i < NX; i++) {        vecA[i] = 1.0;        vecB[i] = 1.0;    }    #pragma acc parallel loop    {		for (int i = 0; i < NX; i++)			vecC[i] = vecA[i] + vecB[i];    }}//// OpenMP//#include <stdio.h>#include <math.h>#define NX 102400int main(void){    double vecA[NX],vecB[NX],vecC[NX];    /* Initialize vectors */    for (int i = 0; i < NX; i++) {        vecA[i] = 1.0;        vecB[i] = 1.0;    }    #pragma omp target teams distribute parallel for simd    {		for (int i = 0; i < NX; i++)			vecC[i] = vecA[i] + vecB[i];	}}