!! OpenAcc!program vecsum    implicit none    integer, parameter :: nx = 102400    real, dimension(:), allocatable :: vecA,vecB,vecC    integer :: i    allocate (vecA(nx), vecB(nx),vecC(nx))    ! Initialization of vectors    do i = 1, nx        vecA(i) = 1.0        vecB(i) = 1.0    end do    !$acc parallel loop    do i=1,nx        vecC(i) = vecA(i) + vecB(i)    enddo    !$acc end parallel loopend program vecsum!! OpenMP!program vecsum    implicit none    integer, parameter :: nx = 102400    real, dimension(nx) :: vecA,vecB,vecC    integer :: i    ! Initialization of vectors    do i = 1, nx        vecA(i) = 1.0        vecB(i) = 1.0    end do    !$omp target teams distribute parallel do simd    do i=1,nx		vecC(i) = vecA(i) + vecB(i)    enddo    !$omp end target teams distribute parallel do simdend program vecsum