// Datatype for temperature fieldstruct field {    // nx and ny are the dimensions of the field. The array data    // contains also ghost layers, so it will have dimensions nx+2 x ny+2    int nx;    int ny;    // Size of the grid cells    double dx;    double dy;    // The temperature values in the 2D grid    std::vector<double> data;};// CONSTANTS// Fixed grid spacingconst double DX = 0.01;const double DY = 0.01;// Default temperaturesconst double T_DISC = 5.0;const double T_AREA = 65.0;const double T_UPPER = 85.0;const double T_LOWER = 5.0;const double T_LEFT = 20.0;const double T_RIGHT = 70.0;// Default problem sizeconst int ROWS = 2000;const int COLS = 2000;const int NSTEPS = 500;